mapper.select:
SELECT
FROM FRI_QRTZ_JobInfo AS t WHERE t.is_deleted = 0
AND t.job_group = #{jobGroup}
AND t.job_key like CONCAT(CONCAT('%', #{jobKey}), '%')
ORDER BY id DESC LIMIT #{offset}, #{pageSize}

SELECT count(1) FROM FRI_QRTZ_JobInfo AS t WHERE t.is_deleted = 0
AND t.job_group = #{jobGroup}
AND t.job_key like CONCAT(CONCAT('%', #{jobKey}), '%')
